import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Spinner, Tabs, Tab } from 'react-bootstrap';

function TrackingHistory() {
    const [playbacks, setPlaybacks] = useState([]);
    const [searches, setSearches] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        Promise.all([
            axios.get('/user-tracking/playbacks'),
            axios.get('/user-tracking/searches')
        ]).then(([playbackRes, searchRes]) => {
            setPlaybacks(playbackRes.data);
            setSearches(searchRes.data);
            setLoading(false);
        }).catch(error => {
            console.error('Error fetching tracking history:', error);
            setLoading(false);
        });
    }, []);

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleString();
    };

    return (
        <div className="tracking-history">
            <h2>Tracking History</h2>
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Loading...</span>
                </Spinner>
            ) : (
                <Tabs defaultActiveKey="playbacks" className="mb-3">
                    <Tab eventKey="playbacks" title="Playback History">
                        <ul className="list-unstyled">
                            {playbacks.map((event, index) => (
                                <li key={index} className="mb-2">
                                    <strong>{event.playback}</strong>
                                    <br />
                                    <small className="text-muted">
                                        {formatDate(event.timestamp)}
                                    </small>
                                </li>
                            ))}
                        </ul>
                    </Tab>
                    <Tab eventKey="searches" title="Search History">
                        <ul className="list-unstyled">
                            {searches.map((event, index) => (
                                <li key={index} className="mb-2">
                                    <strong>{event.searchQuery}</strong>
                                    <br />
                                    <small className="text-muted">
                                        {formatDate(event.timestamp)}
                                    </small>
                                </li>
                            ))}
                        </ul>
                    </Tab>
                </Tabs>
            )}
        </div>
    );
}

export default TrackingHistory;
